const App = () => {
  const course = "Half Stack application development";

  const parts = [
    {
      name: "Fundamentals of React",
      exercises: 10,
    },

    {
      name: "Using props to pass data",
      exercises: 7,
    },

    {
      name: "State of component",
      exercises: 14,
    },
  ];
  console.log(parts.map((p) => p.name + " " + p.exercises + "\n"));
  return (
    <div>
      <Header course={course} />
      <Content
        part={parts.map((p) => p.name + " " + p.exercises + "\n")}
        // exercises1={parts.map((p) => p.exercises)}
      />

      <Total
        a={parts[0].exercises}
        b={parts[1].exercises}
        c={parts[2].exercises}
      />
    </div>
  );
};
export default App;

const Header = (props) => {
  return (
    <>
      <h1>{props.course}</h1>
    </>
  );
};

const Content = (props) => {
  return (
    <ul>
      <Part part={props.part} exercises={props.exercises} />
    </ul>
  );
};

const Total = (props) => {
  return <p>Number of exercises {props.a + props.b + props.c}</p>;
};

const Part = (props) => {
  return (
    <p>
      {props.part} {props.exercises}
    </p>
  );
};
